INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('227','วิชาบังคับก่อน (Prerequisite) ของวิชา กลศาสตร์วิศวกรรม ในหลักสูตรวิศวกรรมไฟฟ้า คือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: ต้องสอบผ่านวิชา 04-020-101 ฟิสิกส์ 1 สำหรับวิศวกร (General Physics 1 for Engineers) มาก่อน\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','วิชาบังคับก่อน, ตัวต่อ, Prerequisite, กลศาสตร์','3','1','2026-02-05 01:18:31','2026-02-09 00:56:02');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('228','วิชาบังคับก่อนของวิชา ระบบควบคุม (Control Systems) ในหลักสูตรวิศวกรรมไฟฟ้า คือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: ต้องสอบผ่านวิชา EN2012311 คณิตศาสตร์วิศวกรรมไฟฟ้า (Electrical Engineering Mathematics) มาก่อน\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','วิชาบังคับก่อน, ตัวต่อ, Prerequisite, คอนโทรล','3','1','2026-02-05 01:18:31','2026-02-09 00:56:02');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('229','วิชาบังคับก่อนของวิชา การวิเคราะห์ระบบไฟฟ้ากำลัง ในหลักสูตรวิศวกรรมไฟฟ้า คือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: ต้องสอบผ่านวิชา EN2012204 ระบบไฟฟ้ากำลัง (Electrical Power Systems) มาก่อน\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','วิชาบังคับก่อน, ตัวต่อ, Prerequisite, PSA','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('230','วิชาบังคับก่อนของวิชา อิเล็กทรอนิกส์กำลัง ในหลักสูตรวิศวกรรมไฟฟ้า คือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: ต้องสอบผ่านวิชา EN2011212 อิเล็กทรอนิกส์วิศวกรรม (Engineering Electronics) หรือ EN2011213 อิเล็กทรอนิกส์วิศวกรรม (รหัสเก่า/เทียบเท่า) มาก่อน\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','วิชาบังคับก่อน, ตัวต่อ, Prerequisite, Power Elec','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('231','วิชาบังคับก่อนของวิชา เครื่องจักรกลไฟฟ้า 2 ในหลักสูตรวิศวกรรมไฟฟ้า คือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: ต้องสอบผ่านวิชา EN2012203 เครื่องจักรกลไฟฟ้า 1 (Electrical Machines 1) มาก่อน\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','วิชาบังคับก่อน, ตัวต่อ, Prerequisite, Machine 2','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('232','วิชาบังคับก่อนของวิชา การป้องกันระบบไฟฟ้ากำลัง ในหลักสูตรวิศวกรรมไฟฟ้า คือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: ต้องสอบผ่านวิชา EN2012308 หรือรายวิชาที่เทียบเท่า การวิเคราะห์ระบบไฟฟ้ากำลัง (Power System Analysis) มาก่อน\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','วิชาบังคับก่อน, ตัวต่อ, Prerequisite, Protection','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('233','วิชาบังคับก่อนของวิชา การวิเคราะห์สนามไฟฟ้าในงานวิศวกรรมไฟฟ้าแรงสูง ในหลักสูตรวิศวกรรมไฟฟ้า คือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: ต้องสอบผ่านวิชา EN2012309 วิศวกรรมไฟฟ้าแรงสูง (High Voltage Engineering) มาก่อน\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','วิชาบังคับก่อน, ตัวต่อ, Prerequisite, High Volt','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('234','วิชาบังคับก่อนของวิชา โครงงานวิศวกรรมไฟฟ้า ในหลักสูตรวิศวกรรมไฟฟ้า คือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: ต้องสอบผ่านวิชา EN2012318 การเตรียมโครงงานวิศวกรรมไฟฟ้า (Preparation for Electrical Engineering Project) มาก่อน\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','วิชาบังคับก่อน, ตัวต่อ, Prerequisite, โปรเจค','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('235','วิชาบังคับก่อนของวิชา สหกิจศึกษาทางวิศวกรรมไฟฟ้า ในหลักสูตรวิศวกรรมไฟฟ้า คือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: 1. ต้องสอบผ่านวิชา EN2013301 การเตรียมความพร้อมสหกิจศึกษา มาก่อน \n2. มีหน่วยกิตสะสมรวมไม่น้อยกว่าเงื่อนไขที่กำหนด\n3. มีเกรดเฉลี่ยสะสม (GPA) ผ่านเกณฑ์ที่คณะกำหนด (ปกติไม่ต่ำกว่า 2.00)\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','วิชาบังคับก่อน, ตัวต่อ, Prerequisite, สหกิจ','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('239','วิชา ST2041103 เคมีสำหรับวิศวกร (ในหลักสูตรวิศวกรรมไฟฟ้า) เรียนอะไร?','ศึกษาโครงสร้างอะตอม ตารางธาตุ พันธะเคมี ปริมาณสารสัมพันธ์ สถานะของสาร (แก๊ส, ของเหลว, ของแข็ง) สารละลาย อุณหพลศาสตร์เคมีเบื้องต้น จลนพลศาสตร์เคมี สมดุลเคมี กรด-เบส เคมีไฟฟ้า และเคมีอินทรีย์เบื้องต้นที่เกี่ยวข้องกับงานวิศวกรรม\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','เคมี, Chemistry, สารเคมี, วิชาพื้นฐาน, ST2041103, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('240','วิชา GE2200101 ภาษาอังกฤษเทคนิค (ในหลักสูตรวิศวกรรมไฟฟ้า) เรียนอะไร?','ศึกษาคำศัพท์และสำนวนภาษาอังกฤษทางเทคนิคที่เกี่ยวข้องกับวิศวกรรม ทักษะการอ่านคู่มือปฏิบัติงาน (Manuals) การอ่านบทความทางวิชาการ การเขียนรายงานเชิงเทคนิค (Technical Reports) การเขียนบันทึกข้อความ และการนำเสนอผลงานทางวิศวกรรม\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','อังกฤษเทคนิค, Technical English, ศัพท์ช่าง, GE2200101, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('241','วิชา GE2300107 กฎหมายและจริยธรรมในวิชาชีพ (ในหลักสูตรวิศวกรรมไฟฟ้า) เรียนอะไร?','ศึกษากฎหมายที่เกี่ยวข้องกับการประกอบวิชาชีพวิศวกรรม (พ.ร.บ. วิศวกร) กฎหมายความปลอดภัยในการทำงาน กฎหมายแรงงาน กฎหมายทรัพย์สินทางปัญญา จรรยาบรรณแห่งวิชาชีพวิศวกรรม ความรับผิดชอบต่อสังคมและสิ่งแวดล้อม และกรณีศึกษาด้านจริยธรรม\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','กฎหมาย, จริยธรรม, จรรยาบรรณ, GE2300107, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('242','วิชา GE2700103 ชีวิตกับเทคโนโลยี (ในหลักสูตรวิศวกรรมไฟฟ้า) เรียนอะไร?','ศึกษาความหมายและความสำคัญของเทคโนโลยี วิวัฒนาการของเทคโนโลยี ผลกระทบของเทคโนโลยีที่มีต่อสังคม เศรษฐกิจ และสิ่งแวดล้อม เทคโนโลยีสารสนเทศและการสื่อสารในชีวิตประจำวัน ความมั่นคงปลอดภัยไซเบอร์ กฎหมายดิจิทัล และเทคโนโลยีเปลี่ยนโลก (Disruptive Technology)\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','เทคโนโลยี, ชีวิตประจำวัน, GE2700103, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('243','วิชา GE2700105 การใช้ชีวิตที่เป็นมิตรกับสิ่งแวดล้อม (Green Living) (ในหลักสูตรวิศวกรรมไฟฟ้า) เรียนอะไร?','ศึกษาความสัมพันธ์ระหว่างมนุษย์กับสิ่งแวดล้อม ปัญหาสิ่งแวดล้อมในปัจจุบัน (ภาวะโลกร้อน, มลพิษ) แนวคิดการพัฒนาที่ยั่งยืน (Sustainable Development) การใช้ทรัพยากรอย่างมีประสิทธิภาพ หลักการ 3R (Reduce, Reuse, Recycle) พลังงานสะอาด และการดำเนินชีวิตแบบเป็นมิตรกับสิ่งแวดล้อม\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','สิ่งแวดล้อม, Green, รักษ์โลก, GE2700105, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('247','วิชา EN2013106 พื้นฐานที่จำเป็นด้านคณิตศาสตร์ สถิติ และคอมพิวเตอร์ สำหรับวิศวกรรมไฟฟ้า เรียนอะไร?','ศึกษาการทบทวนคณิตศาสตร์และสถิติที่จำเป็นสำหรับวิศวกรรมไฟฟ้า (จำนวนเชิงซ้อน, เมทริกซ์, แคลคูลัสเวกเตอร์) ความน่าจะเป็นและการแจกแจงทางสถิติ การวิเคราะห์ข้อมูลเบื้องต้น การใช้ซอฟต์แวร์คอมพิวเตอร์ทางคณิตศาสตร์ (เช่น MATLAB หรือ Scilab) ช่วยในการแก้ปัญหาและแสดงผลกราฟิกทางวิศวกรรม\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','คณิตศาสตร์, สถิติ, คอมพิวเตอร์พื้นฐาน, EN2013106, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('248','วิชา EN2013410 หลักการพื้นฐานทรานเซียนต์ทางไฟฟ้าในระบบไฟฟ้ากำลัง (สาขาวิศวกรรมไฟฟ้า) เรียนอะไร?','ศึกษาปรากฏการณ์ทรานเซียนต์ (Transient) ในระบบไฟฟ้าที่เกิดจากการสวิตชิ่ง (Switching Transients) และฟ้าผ่า (Lightning Transients) ทฤษฎีคลื่นจร (Traveling Waves) บนสายส่ง แผนภาพแลตทิซ (Lattice Diagram) การป้องกันแรงดันเกิน อุปกรณ์กับดักเสิร์จ (Surge Arresters) และการประสานสัมพันธ์ทางฉนวน\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','Transient, ทรานเซียนต์, แรงดันเกิน, ฟ้าผ่า, EN2013410, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('249','วิชา EN2013411 เทคโนโลยีสถานีไฟฟ้า (สาขาวิศวกรรมไฟฟ้า) เรียนอะไร?','ศึกษาประเภทและรูปแบบของสถานีไฟฟ้า (AIS, GIS) การออกแบบผังสถานีไฟฟ้า (Substation Layout) บัสบาร์และการคำนวณ หม้อแปลงวัดค่า (CT, VT) เซอร์กิตเบรกเกอร์และสวิตช์ตัดตอน ระบบควบคุมและระบบป้องกันในสถานีไฟฟ้า (Substation Automation System: SAS) มาตรฐาน IEC 61850 และระบบไฟฟ้าช่วย (Auxiliary Power Supply)\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','สถานีไฟฟ้า, Substation, บัสบาร์, อุปกรณ์ไฟฟ้า, EN2013411, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('250','วิชา EN2013412 วิธีเชิงตัวเลขสำหรับวิศวกรไฟฟ้า (สาขาวิศวกรรมไฟฟ้า) เรียนอะไร?','ศึกษาค่าความคลาดเคลื่อน (Errors) ระเบียบวิธีเชิงตัวเลขสำหรับการหารากของสมการ (Root Finding) การแก้ระบบสมการเชิงเส้นและไมเชิงเส้น การประมาณค่าในช่วง (Interpolation) และการถดถอย (Regression) การหาอนุพันธ์และปริพันธ์เชิงตัวเลข การแก้สมการเชิงอนุพันธ์สามัญ (ODE) และการเขียนโปรแกรมคอมพิวเตอร์เพื่อใช้วิธีเชิงตัวเลขแก้ปัญหาทางวิศวกรรมไฟฟ้า\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','Numerical, เชิงตัวเลข, คำนวณคอมพิวเตอร์, EN2013412, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('258','รหัสวิชา EN2013304 กรณีศึกษาสำหรับวิศวกรรมไฟฟ้า (ในหลักสูตรวิศวกรรมไฟฟ้า) เรียนอะไร?','ศึกษากรณีศึกษาจากงานจริงหรือสถานการณ์จำลองทางวิศวกรรมไฟฟ้า การรวบรวมข้อมูล การวิเคราะห์สาเหตุของปัญหา การสังเคราะห์แนวทางการแก้ไขปัญหา การประยุกต์ใช้ความรู้ทางทฤษฎีเพื่อแก้ปัญหา การเขียนรายงานและนำเสนอผลการวิเคราะห์ (รายวิชานี้สำหรับนักศึกษาที่ไม่เลือกเรียนสหกิจศึกษา)\n\nดูเพิ่มเติม: https://ee.eng.rmutp.ac.th/','curriculum','electrical_engineering','กรณีศึกษา, Case Study, วิชาแทนสหกิจ, EN2013304, คำอธิบายรายวิชา','8','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('341','วิศวกรรมเครื่องกล หมวดวิชาเลือกเสรีเรียนอะไรได้บ้าง?','สามารถเลือกเรียนรายวิชาใดก็ได้ที่เปิดสอนในมหาวิทยาลัยเทคโนโลยีราชมงคลพระนคร ไม่น้อยกว่า 6 หน่วยกิต โดยไม่ซ้ำซ้อนกับรายวิชาที่เคยเรียนมา\n\nดูเพิ่มเติม: https://me.eng.rmutp.ac.th/','curriculum','mechanical_engineering','วิชาเลือกเสรี, เลือกเรียน','14','1','2026-02-05 01:18:31','2026-02-09 01:29:50');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('577','วิชา EN2221107 วงจรไฟฟ้า (Electric Circuits) ของสาขาสื่อสาร เรียนอะไร?','ศึกษาเกี่ยวกับหน่วยและสัญลักษณ์ องค์ประกอบของวงจรไฟฟ้า กฎของโอห์ม กฎของเคอร์ชอฟฟ์ วงจรตัวต้านทาน การวิเคราะห์วงจรด้วยวิธีโนดและวิธีเมช ทฤษฎีบททางวงจรไฟฟ้า (การวางซ้อน, เทเวนิน, นอร์ตัน, การส่งผ่านกำลังไฟฟ้าสูงสุด) ตัวเก็บประจุและตัวเหนี่ยวนำ วงจร RL RC และ RLC ผลตอบสนองธรรมชาติและผลตอบสนองบังคับ เฟสเซอร์ไดอะแกรม และกำลังไฟฟ้าในวงจรไฟฟ้ากระแสสลับ','curriculum','electronics_telecom_engineering','วงจรไฟฟ้า, Circuit, กฎของโอห์ม, Kirchhoff, EN2221107, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('578','วิศวกรรมสื่อสารฯ วิชา EN2221111 สัญญาณและระบบ (Signals and Systems) เรียนเกี่ยวกับอะไร?','ศึกษาการจำแนกประเภทของสัญญาณและระบบ สัญญาณและระบบเวลาต่อเนื่อง สัญญาณและระบบเวลาไม่ต่อเนื่อง ระบบเชิงเส้นที่ไม่แปรเปลี่ยนตามเวลา (LTI System) การแปลงฟูเรียร์ (Fourier Transform) อนุกรมฟูเรียร์ (Fourier Series) การแปลงลาปลาซ (Laplace Transform) การแปลง Z (Z-Transform) และการประยุกต์ใช้งานในทางวิศวกรรมไฟฟ้าสื่อสาร','curriculum','electronics_telecom_engineering','สัญญาณและระบบ, Signals, Systems, Fourier, Laplace, EN2221111, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('579','วิชา EN2221204 สนามแม่เหล็กไฟฟ้า (Electromagnetic Fields) ของสาขาสื่อสาร เรียนอะไร?','ศึกษาการวิเคราะห์เวกเตอร์ กฎของคูลอมบ์ ความเข้มสนามไฟฟ้า ศักย์ไฟฟ้า กฎของเกาส์ ตัวนำและไดอิเล็กทริก ความจุไฟฟ้า กฎของบิโอต์-ซาวารต์ กฎวงจรของแอมแปร์ สนามแม่เหล็กคงตัว แรงแม่เหล็ก วัสดุแม่เหล็ก ความเหนี่ยวนำ สมการของแมกซ์เวลล์ (Maxwell\'s Equations) และคลื่นแม่เหล็กไฟฟ้า','curriculum','electronics_telecom_engineering','สนามแม่เหล็ก, Field, Maxwell, คลื่นแม่เหล็กไฟฟ้า, EN2221204, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('580','วิชา EN2222301 หลักการระบบสื่อสาร (Principles of Communication Systems) เรียนอะไร?','ศึกษาทฤษฎีพื้นฐานของการสื่อสาร การวิเคราะห์สเปกตรัม การมอดูเลตแบบแอนะล็อก (AM, FM, PM) สัญญาณรบกวนในระบบสื่อสาร การมอดูเลตแบบพัลส์ (PAM, PWM, PPM) การมอดูเลตแบบดิจิทัล (ASK, FSK, PSK, QAM) ทฤษฎีการสุ่มสัญญาณ (Sampling Theory) และหลักการมัลติเพล็กซ์ (Multiplexing)','curriculum','electronics_telecom_engineering','ระบบสื่อสาร, Communication, Modulation, AM, FM, Digital Comm, EN2222301, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('581','วิชา EN2222303 การสื่อสารข้อมูลและเครือข่าย (Data Communications and Networks) เรียนอะไร?','ศึกษาแบบจำลองมาตรฐาน OSI และ TCP/IP สื่อสัญญาณและการส่งข้อมูล การเข้ารหัสข้อมูล การควบคุมการเชื่อมโยงข้อมูล (Data Link Control) โปรโตคอลเครือข่าย (Network Protocols) การจัดเส้นทาง (Routing) เครือข่ายแลน (LAN) เครือข่ายแวน (WAN) เครือข่ายไร้สาย และความปลอดภัยในระบบเครือข่าย','curriculum','electronics_telecom_engineering','Data Comm, Network, เครือข่าย, OSI Model, TCP/IP, EN2222303, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('582','วิศวกรรมสื่อสารฯ วิชา EN2222304 วิศวกรรมไมโครเวฟและสายอากาศ เรียนอะไร?','ศึกษาทฤษฎีสายส่งสัญญาณ (Transmission Lines) แผนภูมิสมิธ (Smith Chart) ท่อนำคลื่น (Waveguides) อุปกรณ์ไมโครเวฟ พารามิเตอร์ของสายอากาศ (Gain, Radiation Pattern, Impedance) ชนิดของสายอากาศ (Dipole, Yagi-Uda, Microstrip Antenna) การแผ่กระจายคลื่นวิทยุ และการออกแบบระบบสื่อสารไมโครเวฟ','curriculum','electronics_telecom_engineering','ไมโครเวฟ, สายอากาศ, Antenna, Microwave, Smith Chart, EN2222304, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('583','วิชา EN2222308 ปัญญาประดิษฐ์สำหรับวิศวกรรมไฟฟ้าสื่อสาร (Artificial Intelligence for Electrical Communication Engineering) เรียนอะไร?','ศึกษาหลักการพื้นฐานของปัญญาประดิษฐ์ (AI) การเรียนรู้ของเครื่อง (Machine Learning) โครงข่ายประสาทเทียม (Artificial Neural Networks) การเรียนรู้เชิงลึก (Deep Learning) การประมวลผลสัญญาณและการรู้จำรูปแบบ (Pattern Recognition) และการประยุกต์ใช้ AI ในระบบสื่อสารและระบบอัจฉริยะ','curriculum','electronics_telecom_engineering','AI, ปัญญาประดิษฐ์, Machine Learning, Deep Learning, EN2222308, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('584','วิศวกรรมสื่อสารฯ วิชา EN2222309 อินเทอร์เน็ตของสรรพสิ่ง (IoT) เรียนอะไร?','ศึกษาสถาปัตยกรรมและองค์ประกอบของ IoT เซนเซอร์และอุปกรณ์ตรวจจับ บอร์ดสมองกลฝังตัวสำหรับ IoT (เช่น Arduino, ESP32, Raspberry Pi) โปรโตคอลสื่อสารสำหรับ IoT (MQTT, CoAP, HTTP) การเชื่อมต่อกับคลาวด์ (Cloud Computing) การจัดการข้อมูล Big Data เบื้องต้น และกรณีศึกษาการประยุกต์ใช้งาน IoT','curriculum','electronics_telecom_engineering','IoT, Internet of Things, เซนเซอร์, Smart Home, EN2222309, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('585','วิชา EN2221207 ระบบควบคุม (Control Systems) ของสาขาสื่อสาร เรียนอะไร?','ศึกษาแบบจำลองทางคณิตศาสตร์ของระบบ ฟังก์ชันถ่ายโอน แผนภาพบล็อก กราฟการไหลของสัญญาณ ผลตอบสนองของระบบ เสถียรภาพของระบบ (Routh-Hurwitz, Root Locus, Bode Plot) การออกแบบตัวควบคุม PID และการประยุกต์ใช้ในระบบอัจฉริยะ','curriculum','electronics_telecom_engineering','ระบบควบคุม, Control System, PID, Stability, EN2221207, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('586','วิศวกรรมสื่อสารฯ วิชา EN2221208 ไมโครคอนโทรลเลอร์และการอินเตอร์เฟส เรียนอะไร?','ศึกษาสถาปัตยกรรมของไมโครคอนโทรลเลอร์ ชุดคำสั่งและการเขียนโปรแกรมควบคุม (Assembly/C Language) พอร์ตอินพุต/เอาต์พุต (GPIO) การเชื่อมต่อกับอุปกรณ์ภายนอก (LED, Switch, LCD, Sensor, Motor) การใช้งาน Timer/Counter, Interrupt, ADC, UART และการประยุกต์ใช้งานในระบบฝังตัว','curriculum','electronics_telecom_engineering','ไมโครคอนโทรลเลอร์, MCU, Embedded, Arduino, การเขียนโปรแกรม, EN2221208, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('587','วิศวกรรมสื่อสารฯ วิชา EN2221109 วงจรดิจิทัลและลอจิก เรียนอะไร?','ศึกษาระบบเลขฐาน พีชคณิตบูลีน ลอจิกเกต วงจรคอมบิเนชัน (Combinational Circuits) การลดรูปวงจร (Karnaugh Map) วงจรซีเควนเชียล (Sequential Circuits) ฟลิปฟลอป วงจรนับ วงจรลงทะเบียน และการออกแบบวงจรดิจิทัลพื้นฐาน','curriculum','electronics_telecom_engineering','ดิจิทัล, Digital, Logic Gate, วงจรลอจิก, EN2221109, คำอธิบายรายวิชา','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
INSERT INTO faq (id,question,answer,category,department,keywords,view_count,is_active,created_at,updated_at) VALUES ('591','วิชาบังคับก่อนของวิชา ระบบควบคุม (Control Systems) ในสาขาสื่อสารคือวิชาอะไร?','เงื่อนไขวิชาบังคับก่อน: ต้องสอบผ่านรายวิชา EN2221105 คณิตศาสตร์วิศวกรรมไฟฟ้าสื่อสาร (Electrical Communication Engineering Mathematics) มาก่อน','curriculum','electronics_telecom_engineering','วิชาบังคับก่อน, Prerequisite, ตัวต่อ, คอนโทรล','0','1','2026-02-05 01:18:31','2026-02-05 01:18:31');
